import React, { createContext, useContext, useState, useEffect } from 'react';

const ProductContext = createContext();

export const useProducts = () => {
  const context = useContext(ProductContext);
  if (!context) {
    throw new Error('useProducts must be used within a ProductProvider');
  }
  return context;
};

export const ProductProvider = ({ children }) => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  // Initialize with sample DJ products
  useEffect(() => {
    const sampleProducts = [
      {
        id: 1,
        name: 'Pioneer DJ DDJ-400 Controller',
        price: 299.99,
        category: 'Controllers',
        description: 'Professional 2-channel DJ controller with Rekordbox integration',
        image: 'https://images.unsplash.com/photo-1511379938547-c1f69419868d?w=400&h=300&fit=crop',
        stock: 15
      },
      {
        id: 2,
        name: 'Technics SL-1200MK7 Turntable',
        price: 999.99,
        category: 'Turntables',
        description: 'Classic direct drive turntable with high-torque motor',
        image: 'https://images.unsplash.com/photo-1493225457124-a3eb161ffa5f?w=400&h=300&fit=crop',
        stock: 8
      },
      {
        id: 3,
        name: 'Shure SM7B Microphone',
        price: 399.99,
        category: 'Microphones',
        description: 'Dynamic microphone with excellent sound quality for vocals',
        image: 'https://images.unsplash.com/photo-1589003077984-894e1322bea9?w=400&h=300&fit=crop',
        stock: 12
      },
      {
        id: 4,
        name: 'Behringer X32 Digital Mixer',
        price: 2499.99,
        category: 'Mixers',
        description: '32-channel digital mixer with advanced features',
        image: 'https://images.unsplash.com/photo-1511379938547-c1f69419868d?w=400&h=300&fit=crop',
        stock: 5
      },
      {
        id: 5,
        name: 'JBL Professional EON615 Speakers',
        price: 399.99,
        category: 'Speakers',
        description: 'Powered loudspeaker with 1000W power output',
        image: 'https://images.unsplash.com/photo-1493225457124-a3eb161ffa5f?w=400&h=300&fit=crop',
        stock: 20
      },
      {
        id: 6,
        name: 'Sennheiser HD 25 Headphones',
        price: 149.99,
        category: 'Headphones',
        description: 'Professional DJ headphones with excellent isolation',
        image: 'https://images.unsplash.com/photo-1589003077984-894e1322bea9?w=400&h=300&fit=crop',
        stock: 25
      },
      {
        id: 7,
        name: 'Numark Mixtrack Pro 3',
        price: 199.99,
        category: 'Controllers',
        description: '2-channel DJ controller with built-in sound card',
        image: 'https://images.unsplash.com/photo-1511379938547-c1f69419868d?w=400&h=300&fit=crop',
        stock: 18
      },
      {
        id: 8,
        name: 'Roland TR-8S Drum Machine',
        price: 699.99,
        category: 'Drum Machines',
        description: 'Analog circuit behavior drum machine with sampling',
        image: 'https://images.unsplash.com/photo-1493225457124-a3eb161ffa5f?w=400&h=300&fit=crop',
        stock: 10
      }
    ];
    setProducts(sampleProducts);
    setLoading(false);
  }, []);

  const getProductById = (id) => {
    return products.find(product => product.id === parseInt(id));
  };

  const getProductsByCategory = (category) => {
    return products.filter(product => product.category === category);
  };

  const addProduct = (product) => {
    const newProduct = {
      ...product,
      id: products.length + 1
    };
    setProducts([...products, newProduct]);
  };

  const updateProduct = (id, updatedProduct) => {
    setProducts(products.map(product => 
      product.id === id ? { ...product, ...updatedProduct } : product
    ));
  };

  const deleteProduct = (id) => {
    setProducts(products.filter(product => product.id !== id));
  };

  const getCategories = () => {
    return [...new Set(products.map(product => product.category))];
  };

  const value = {
    products,
    loading,
    getProductById,
    getProductsByCategory,
    addProduct,
    updateProduct,
    deleteProduct,
    getCategories
  };

  return (
    <ProductContext.Provider value={value}>
      {children}
    </ProductContext.Provider>
  );
};
